// Simple signal generation for testing
const input = $input.first().json;

// Generate random scores for testing
const technicalScore = Math.floor(Math.random() * 40) + 60; // 60-100
const greeksScore = Math.floor(Math.random() * 30) + 70; // 70-100
const sentimentScore = Math.floor(Math.random() * 20) + 80; // 80-100

const totalScore = (technicalScore * 0.4 + greeksScore * 0.3 + sentimentScore * 0.3);

const signal = totalScore >= 70 ? 'BUY' : 'WAIT';
const strategy = input.strategy || 'IRON_CONDOR';
const symbol = input.symbol || 'NIFTY';

return {
  test_mode: true,
  timestamp: new Date().toISOString(),
  symbol: symbol,
  strategy: strategy,
  scores: {
    technical: technicalScore,
    greeks: greeksScore,
    sentiment: sentimentScore,
    total: totalScore.toFixed(2)
  },
  signal: signal,
  recommendation: signal === 'BUY' ? 
    `Execute ${strategy} on ${symbol}` : 
    `Wait for better opportunity on ${symbol}`,
  mock_order: signal === 'BUY' ? {
    symbol: `${symbol}24DEC21500CE`,
    quantity: 50,
    order_type: 'LIMIT',
    price: 150,
    strategy: strategy
  } : null
};
